import React, { useState } from 'react';
import {ActionCreator} from "../../store/actions"
import {useSelector, useDispatch} from 'react-redux';
import {Table} from '../table/table';
import {dataStateSelector, sortDataSelector, usersDataSelector} from '../../store/selectors'
import {Preloader} from '../preloader/preloader';
import {AddUser} from"../add-user-form/add-user-form";
import {SearchBar} from '../search-bar/search-bar';
import {Pagination} from '../pagination/pagination';


export const UsersPage = () => {
    const users = useSelector(sortDataSelector);

    const [posts, setPosts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage] = useState(50);
    setPosts(users);
  
    console.log(setPosts.length)
    
    const indexOfLastPost = currentPage * postsPerPage;
    const indexOfFirstPost = indexOfLastPost - postsPerPage;
    const currentUsers = posts.slice(indexOfFirstPost, indexOfLastPost);

    const paginate = pageNumber => setCurrentPage(pageNumber);
    
    const isUsersLoaded = useSelector(dataStateSelector);
return(
isUsersLoaded ? 
        <>
        <SearchBar/>
        <AddUser/>
        <Table users={currentUsers}/>
        <Pagination
        usersPerPage={postsPerPage}
        totalUsers={users.length}
        paginate={paginate}/>
        </>
    : 
    <Preloader/>
  )    
}
