import Namespace from './reducers/name-space';
import {createSelector} from 'reselect';

const sortSelector = (state) => state.sortBy;
const usersSelector= (state) => state.usersData;
const filterSelector = (state) => state.textFilter;

export const sortDataSelector = createSelector(
    sortSelector,
    usersSelector,
    (sortSelector, usersSelector) =>
    { 
        if(usersSelector != null){
            return [...usersSelector].sort(function (a, b) {
                if(sortSelector.sortType === 'asc'){
                    return a[sortSelector.sortField] > b[sortSelector.sortField] ? 1 : -1;
                }
               else if(sortSelector.sortType === 'desc'){
                    return a[sortSelector.sortField] < b[sortSelector.sortField] ? 1 : -1;
               }  
              });
        }
        return usersSelector
    }
)


