import React, { useState, useEffect, useCallback }  from 'react';
import {ActionCreator} from "../../store/actions"
import {useSelector, useDispatch} from 'react-redux';
import { useHistory } from "react-router-dom";
import {SortLink} from "./sort_link";
import {AddUser} from"../addUser/addUser";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { compose } from "recompose";
import { getSortedDevelopersCollection, sortSelector } from "../../store/selectors";

function Table ({ collection, setSortParams, clearSortParams, sortParams }) {
  const data = useSelector((state) => state.data);
  const dispatch = useDispatch();

  if(data != null){
  return(
    <>
    <AddUser></AddUser>
    <th>
      <SortLink
        label="First Name"
        sortKey="firstName"
        sort={sortParams}
        onSort={(key, type) => setSortParams(key, type)}
        onSortClear={() => clearSortParams()}
        />
    </th>
     <th>
       <SortLink
         label="Last Name"
         sortKey="lastName"
         sort={sortParams}
         onSort={(key, type) => setSortParams(key, type)}
         onSortClear={() => clearSortParams()}
       />
     </th>
    <table className="table">
    <tbody>
    {data.map(item => (
      <tr key={item.phone} className="people__row">
        <th>{item.id}</th>
        <th>{item.firstName}</th>
        <th>{item.lastName}</th>
        <th>{item.email}</th>
        <th>{item.phone}</th>
        <button onClick={(e) => dispatch(ActionCreator.removeUser(item.id))}>Delete</button>
      </tr>
    ))}
  </tbody>
  </table>
  </>
  )
  }
  return(<div class="lds-ring"><div></div><div></div><div></div><div></div></div>
  )
}


const mapStateToProps = (state, props) => ({
  collection: getSortedDevelopersCollection(state),
  sortParams: sortSelector(state)
});

const mapDispatchToProps = dispatch => ({
  setSortParams: bindActionCreators(ActionCreator.setSortParams, dispatch),
  clearSortParams: bindActionCreators(ActionCreator.clearSortParams, dispatch)
});

const enhance = compose(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )
);
export default enhance(Table);
