import Namespace from './reducers/name-space';
import {createSelector} from 'reselect';

const sortSelector = (state) => state.sortBy;
const usersSelector= (state) => state.usersData;
const filterSelector = (state) => state.textFilter;

export const sortDataSelector = createSelector(
    sortParams,
    usersData,
    (sortParams, usersData) =>
    { 
        if(usersData != null){
            return [...usersData].sort(function (a, b) {
                if(sortParams.sortType === 'asc'){
                    return a[sortParams.sortField] > b[sortParams.sortField] ? 1 : -1;
                }
               else if(sortParams.sortType === 'desc'){
                    return a[sortParams.sortField] < b[sortParams.sortField] ? 1 : -1;
               }  
              });
        }
        return usersData
    }
)


export const filterDataSelector = createSelector(
    usersData,
    filterParams,
    (usersData, filterParams) => {
        if(usersData != null){
            return console.log([...usersData].filter((user) => user.lastName.toLowerCase().includes(filterParams.toLowerCase())))
        }
    }
);
