import React, { useState }  from 'react';
import {ActionCreator} from "../../store/actions"
import {useSelector, useDispatch} from 'react-redux';
import { useHistory } from "react-router-dom";





function TerminalList(id){
    const terminals = useSelector((state) => state.terminal.list);
    const dispatch = useDispatch();
    
    
    function changeTerminal(item){
        let bool = item.isActive;
        bool = !bool;
        console.log(item);
      dispatch(ActionCreator.changeTerminal(item.id));
    }

    function deleteTerminal(){
        dispatch(ActionCreator.deleteTerminal())
    }
    return(
        <div>
            <button onClick={() => deleteTerminal()}>Удалить</button>

            {terminals.map(item=>(
                <div className="terminal__item" key={item.id}>
                    <input type="checkbox" defaultChecked={item.isActive} onChange={() => changeTerminal(item)}/>
                    <div>{item.name}, </div>
                    <div>{item.description}</div>
                </div>
            ))}
        </div>
    )

}

export default TerminalList;
