import React, { useState, useRef, useCallback }  from 'react';
import {ActionCreator} from "../../store/actions"
import {useSelector, useDispatch} from 'react-redux';
import { useHistory } from "react-router-dom";
import TerminalList from './terminalList.jsx';





export const TerminalForm = () => {
    const dispatch = useDispatch();
    
    const name = useRef(null);
    const description = useRef(null);
    const id = useRef(null);

    const onSubmit = useCallback((e)=> {
        e.preventDefault();
        dispatch(ActionCreator.setTerminal({
            id: id.current.value,
            name: name.current.value,
            description: description.current.value,
            isActive: false
        }));
        id.current.value = '';
        name.current.value = '';
        description.current.value = '';
        return false;
    }, [dispatch, name, description]);

    return(
        <>
        <form className="b-form" 
        onSubmit={onSubmit}>
             <div>
                <label >Номер терминала</label>
                <input type="text" ref={id}></input>
            </div>
            <div>
                <label >Название терминала</label>
                <input type="text" ref={name}></input>
            </div>
            <div>
                <label>Описание</label>
                <input type="text" ref={description}></input>
            </div>
            <button>Добавить</button>
        </form>
        <TerminalList></TerminalList>

        </>

    )   
}
