import Namespace from './reducers/name-space';
import {createSelector} from 'reselect';

const sortParamsSelector = (state) => state.sortBy;
const usersDataSelector = (state) => state.usersData;
const filterParamSelector = (state) => state.textFilter;

export const sortDataSelector = createSelector(
    sortParamsSelector,
    usersDataSelector,
    (sortParams, users) =>
    { 
        if(users != null){
            return [...users].sort(function (a, b) {
                if(sortParams.sortType === 'asc'){
                    return a[sortParams.sortField] > b[sortParams.sortField] ? 1 : -1;
                }
               else if(sortParams.sortType === 'desc'){
                    return a[sortParams.sortField] < b[sortParams.sortField] ? 1 : -1;
               }  
              });
        }
        return usersData
    }
)


export const filterDataSelector = createSelector(
    usersDataSelector,
    filterParamSelector,
    (usersData, filterParam) => {
        if(usersData != null){
            return [...usersData].filter((user) => user.lastName.toLowerCase().includes(filterParam.toLowerCase())) 
        }
    }
);
