import React, { useState } from 'react';
import {ActionCreator} from "../../store/actions"
import {useSelector, useDispatch} from 'react-redux';
import {SortLink} from "./sort-link";
import {AddUser} from"../add-user-form/add-user-form";
import {SearchBar} from '../search-bar/search-bar';
import {dataStateSelector, sortDataSelector} from '../../store/selectors'
import {Preloader} from '../preloader/preloader';
import {UserCard} from '../user-card/user-card';



export const Table = () => {
  const dispatch = useDispatch();
  const sortData = useSelector(sortDataSelector);
  const isUsersLoaded = useSelector(dataStateSelector);
  const [user, setUser] = useState('');
  console.log(user);
  if(isUsersLoaded != false){
  return(
    <>
    <SearchBar/>
    <AddUser/>
    <table className="table">
    <thead>
     <tr>
      <SortLink
        label="Id"
        sortField="id"
        />
      <SortLink
        label="First Name"
        sortField="firstName"
        />
       <SortLink
         label="Last Name"
         sortField="lastName"
       />
       <SortLink
         label="Email"
         sortField="email"
       />
       <SortLink
         label="Phone"
         sortField="phone"
       />
     </tr>
    </thead>
    <tbody>
    {sortData.map(item => (
      <tr key={item.phone} onClick={() => setUser(item)} className="people__row">
        <td>{item.id}</td>
        <td>{item.firstName}</td>
        <td>{item.lastName}</td>
        <td>{item.email}</td>
        <td>{item.phone}</td>
        <button onClick={(e) => dispatch(ActionCreator.removeUser(item.id))}>Delete</button>
      </tr>
    ))}
  </tbody>
  </table>
  {user ? <UserCard user={user}/> : null}
  </>
  )
  }
  return(
    <Preloader/>
  )
}
