import React, { useState, useEffect, useCallback }  from 'react';
import {ActionCreator} from "../../store/actions"
import {useSelector, useDispatch} from 'react-redux';
import { useHistory } from "react-router-dom";
import {SortLink} from "./sort_link";
import {AddUser} from"../addUser/addUser";

export const Table = () => {
  const data = useSelector((state) => state.data);
  const dispatch = useDispatch();

  if(data != null){
  return(
    <>
    <AddUser></AddUser>
    <th>
      <SortLink
        label="Id"
        sortField="id"
        sortType="asc"
        />
    </th>
    <th>
      <SortLink
        label="First Name"
        sortField="firstName"
        sortType="asc"
        />
    </th>
     <th>
       <SortLink
         label="Last Name"
         sortField="lastName"
       />
     </th>
    <table className="table">
    <tbody>
    {data.map(item => (
      <tr key={item.phone} className="people__row">
        <th>{item.id}</th>
        <th>{item.firstName}</th>
        <th>{item.lastName}</th>
        <th>{item.email}</th>
        <th>{item.phone}</th>
        <button onClick={(e) => dispatch(ActionCreator.removeUser(item.id))}>Delete</button>
      </tr>
    ))}
  </tbody>
  </table>
  </>
  )
  }
  return(<div className="lds-ring"><div></div><div></div><div></div><div></div></div>
  )
}
