import React, { useState, useEffect, useCallback, useRef }  from 'react';
import {ActionCreator} from "../../store/actions"
import {useSelector, useDispatch} from 'react-redux';
import { useHistory } from "react-router-dom";



export const AddUser = () => {
    const id = useRef(null);
    const firstName = useRef(null);
    const lastName = useRef(null);
    const mail = useRef(null);
    const phone = useRef(null);
    const dispatch = useDispatch();

    const onSubmit = useCallback((e)=> {
        e.preventDefault();
        dispatch(ActionCreator.addUser({
            id: id.current.value,
            firstName: firstName.current.value,
            lastName: lastName.current.value,
            mail: mail.current.value,
            phone: phone.current.value
        }));
        id.current.value = '';
        firstName.current.value = '';
        lastName.current.value = '';
        phone.current.value = '';
        mail.current.value = '';
        return false;
    }, [dispatch, id, firstName, lastName, mail, phone]);


    return(
       <form onSubmit={onSubmit}>
        <input ref={id}/><label>ID</label>
        <input ref={firstName}/><label>Имя</label>
        <input ref={lastName}/><label>Фамилия</label>
        <input ref={mail}/><label>Почта</label>
        <input ref={phone}/><label>Телефон</label>
        <button>Добавить</button> 
       </form> 
    )
}
