import Namespace from './reducers/name-space';
import {createSelector} from 'reselect';

const sortBy = (state) => state.sortBy;
const usersData = (state) => state.usersData;
const filterBy = (state) => state.textFilter;

export const sortDataSelector = createSelector(
    sortBy,
    usersData,
    (sortBy, usersData) =>
    {   

        if(usersData != null){
            return [...usersData].sort(function (a, b) {
                if(sortBy.sortType === 'asc'){
                    return a[sortBy.sortField] > b[sortBy.sortField] ? 1 : -1;
                }
               else if(sortBy.sortType === 'desc'){
                    return a[sortBy.sortField] < b[sortBy.sortField] ? 1 : -1;
               }  
              });
        }
        return usersData
    }
)


export const filterDataSelector = createSelector(
    usersData,
    filterBy,
    (usersData, filterBy) => {
        if(usersData != null){
            return [...usersData].filter((user) => console.log(user)) 
        }
        return usersData;
    }
);
