import Namespace from './reducers/name-space';
import {createSelector} from 'reselect';

const sortBy = (state) => state.sortBy;
const usersData = (state) => state.usersData;


export const sortDataSelector = createSelector(
    sortBy,
    usersData,
    (sortBy, usersData) =>
    {   

        if(usersData != null){
            return [...usersData].sort(function (a, b) {
                if(sortBy.sortType === 'asc'){
                    if (a[sortBy.sortField] > b[sortBy.sortField]) {
                        return 1;
                    }
                }
               else if(sortBy.sortType === 'desc'){
                if (a[sortBy.sortField] < b[sortBy.sortField]) {
                    return -1;
                  }
               }  
                return 0;
              });
        }
        return usersData
    }
)
