    console.log(user)
    return(
        <>
            Выбран пользователь <b>{user.firstName} {user.lastName}</b>
            Описание:
            <textarea>
            {user.description}
            </textarea>
            Адрес проживания: <b>9792 Mattis Ct</b>
            Город: <b>Waukesha</b>
            Провинция/штат: <b>WI</b>
            Индекс: <b>22178</b>
        </>
    )
}
